///@file    Rotate Function
///@author  Wei Zhao
///@date    2016.09.18
///@version 1.0

/*
 Given an array of integers A and let n to be its length.
 
 Assume Bk to be an array obtained by rotating the array A k positions clock-wise, we define a "rotation function" F on A as follow:
 
 F(k) = 0 * Bk[0] + 1 * Bk[1] + ... + (n-1) * Bk[n-1].
 
 Calculate the maximum value of F(0), F(1), ..., F(n-1).
 
 Note:
 n is guaranteed to be less than 105.
 
 Example:
 
 A = [4, 3, 2, 6]
 
 F(0) = (0 * 4) + (1 * 3) + (2 * 2) + (3 * 6) = 0 + 3 + 4 + 18 = 25
 F(1) = (0 * 6) + (1 * 4) + (2 * 3) + (3 * 2) = 0 + 4 + 6 + 6 = 16
 F(2) = (0 * 2) + (1 * 6) + (2 * 4) + (3 * 3) = 0 + 6 + 8 + 9 = 23
 F(3) = (0 * 3) + (1 * 2) + (2 * 6) + (3 * 4) = 0 + 2 + 12 + 12 = 26
 
 So the maximum value of F(0), F(1), F(2), F(3) is F(3) = 26.
 */

#include <vector>
#include <cmath>
#include <algorithm>
using namespace std;

class Solution_tle {
public:
    ///@brief   find the max value if the function was imposed on the vector A
    ///@param   A   array of integers
    ///@return  the maximum value of function on A
    ///@note    1. naive algorithm, the time complexity is O(n^2). It coult possibly tle. After test, it was tle.
        int maxRotateFunction(vector<int>& A) {
        if (A.empty())  return 0;
        int n = A.size();
        int max_val = INT_MIN;
        for (int i = 0; i < n; i++) {
            max_val = max(max_val, funcA(A, i));
        }
        return max_val;
    }
    
    ///@brief   function on A
    ///@param   A   array of original integers
    ///@param   offset the offset of clock-wise move
    ///@return  the function value on the transformed A.
    int funcA(vector<int> A, int offset) {
        vector<int> B;
        int rslt = 0;
        for (int i = A.size() - offset; i < A.size(); i++) {
            B.push_back(A[i]);
        }
        for (int i = 0; i < A.size() - offset; i++) {
            B.push_back(A[i]);
        }
        for (int i = 0; i < B.size(); i++) {
            rslt += i * B[i];
        }
        return rslt;
    }
};

class Solution {
public:
    /*
     F(k) = 0 * Bk[0] + 1 * Bk[1] + ... + (n-1) * Bk[n-1]
     F(k-1) = 0 * Bk-1[0] + 1 * Bk-1[1] + ... + (n-1) * Bk-1[n-1]
     = 0 * Bk[1] + 1 * Bk[2] + ... + (n-2) * Bk[n-1] + (n-1) * Bk[0]
     Then,
     
     F(k) - F(k-1) = Bk[1] + Bk[2] + ... + Bk[n-1] + (1-n)Bk[0]
     = (Bk[0] + ... + Bk[n-1]) - nBk[0]
     = sum - nBk[0]
     Thus,
     
     F(k) = F(k-1) + sum - nBk[0]
     What is Bk[0]?
     
     k = 0; B[0] = A[0];
     k = 1; B[0] = A[len-1];
     k = 2; B[0] = A[len-2];
     ...
     
     Time: O(n)
     */
    int maxRotateFunction(vector<int>& A) {
        if (A.empty())  return 0;
        int max_val = INT_MIN;
        int sum = 0;
        for (int i = 0; i != A.size(); i++) {
            sum += A[i];
        }
        int fk_1 = 0;
        for (int i = 0; i != A.size(); i++) {
            fk_1 = fk_1 + i * A[i];
        }
        max_val = max(max_val, fk_1);
        int fk = 0;
        for (int i = 1; i != A.size(); i++) {
            fk = sum + fk_1 - A[A.size() - i] * A.size();
            max_val = max(max_val, fk);
            fk_1 = fk;
        }
        return max_val;
    }
};

